import React from "react";

export const ContactPicker = ( {id, value, contacts, onChange} ) => {
  return (
    <select id={id} value={value} onChange={onChange} >
      <option key='0' value=''>
        No Contact Selected
      </option>
      {contacts?.map((contact,i) =>
        <option key={i+1} value={contact.name}>
          {contact.name}
        </option>
      )}
    </select>
  );
};
